"use strict";var cnt=0,addTodo=function(){var t="remove"+cnt,o=$("#todoInput").val();if(1<=o.trim().length){var d='<li class="todo__item"><div class="todo__item-title">'+o+'</div><button id="'+t+'"class="todo__item-remove">X</button></li>';$(".todo__list").append(d),$("#"+t).click(function(){$(this).parent(".todo__item").remove()}),$(".todo__input").val(""),cnt++}};$(".add").click(function(){addTodo()}),$("#todoInput").keydown(function(t){13==t.keyCode&&addTodo()});